package com.example.foodrecyclerview;

import android.os.Bundle;
import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import com.example.foodrecyclerview.databinding.ActivityMainBinding;

import java.util.ArrayList;

public class MainActivity extends AppCompatActivity {

    private ActivityMainBinding binding;
    private ArrayList<Food> foodList;
    private FoodAdapter adapter;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);

        binding = ActivityMainBinding.inflate(getLayoutInflater());
        setContentView(binding.getRoot());

        binding.recyclerView.setLayoutManager(new LinearLayoutManager(this));

        foodList = new ArrayList<>();
        populateFoodList();

        adapter = new FoodAdapter(foodList);
        binding.recyclerView.setAdapter(adapter);
    }

    private void populateFoodList() {
        foodList.add(new Food("Пицца", "data:image/jpeg;base64,", "Итальянское блюдо с томатным соусом и сыром"));
        foodList.add(new Food("Бургер", "https://example.com/burger.jpg", "Американская классика с говяжьей котлетой"));
        foodList.add(new Food("Суши", "https://example.com/sushi.jpg", "Японское блюдо с рисом и рыбой"));
        foodList.add(new Food("Паста", "https://example.com/pasta.jpg", "Традиционная итальянская паста с соусом"));
        foodList.add(new Food("Салат", "https://example.com/salad.jpg", "Легкий и полезный овощной салат"));
        foodList.add(new Food("Фрукты", "https://example.com/fruits.jpg", "Свежие сезонные фрукты"));
        foodList.add(new Food("Мороженое", "https://example.com/ice_cream.jpg", "Освежающее ванильное мороженое"));
        foodList.add(new Food("Стейк", "https://example.com/steak.jpg", "Сочный стейк средней прожарки"));
        foodList.add(new Food("Курица", "https://example.com/chicken.jpg", "Запеченная куриная грудка с травами"));
        foodList.add(new Food("Суп", "https://example.com/soup.jpg", "Традиционный куриный суп с овощами"));
        foodList.add(new Food("Шашлык", "https://example.com/bbq.jpg", "Маринованное мясо, приготовленное на углях"));
        foodList.add(new Food("Панкейки", "https://example.com/pancakes.jpg", "Американские блинчики с сиропом"));
        foodList.add(new Food("Хот-дог", "https://example.com/hotdog.jpg", "Булочка с сосиской и горчицей"));
        foodList.add(new Food("Чизкейк", "https://example.com/cheesecake.jpg", "Нежный сырный десерт с клубникой"));
        foodList.add(new Food("Картофель фри", "https://example.com/fries.jpg", "Хрустящий картофель с солью"));
    }
}
